import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { FlightService } from './../api/services/flight.service';
import { FlightRm } from '../api/models';


@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css']
})
export class BookFlightComponent implements OnInit {

  constructor(private route: ActivatedRoute, private flightService: FlightService) {

  }
  flightId: string = "not loaded"
  flight: FlightRm = {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(p => this.findFlight(p.get("flightId")));
  }

  private findFlight = (flightId: string | null)=>{
    this.flightId = flightId ?? "not passed";

    this.flightService.findFlight({ id: this.flightId })
      .subscribe(flight => this.flight = flight)
  }
}

<div class="container d-grid gap-3" style="max-width: 700px;">

  <div class="form-floating">
    <input placeholder="Email" class="form-control" type="text" />
    <label for="email">Email</label>

  </div>
  <div class="d-grid gap-3">

    <div class="form-floating">

      <input class="form-control" type="text" id="firstName" placeholder="First Name" />
      <label class="form-label" for="firstName">First Name</label>

    </div>

    <div class="form-floating">

      <input placeholder="Last Name" class="form-control" id="lastName" type="text" />
      <label class="form-label" for="lastName">Last Name</label>

    </div>


    <div>

      <input id="isFemale" class="form-check-input" [value]=true type="radio" />
      <label class="ms-2 form-check-label" for="isFemale">Female</label>
    </div>
    <div>
      <input id="isFemale" class="form-check-input" [value]=false type="radio" />
      <label class="ms-2 form-check-label" for="isFemale">Male</label>
    </div>
  </div>
  <div class="d-flex justify-content-center center">
    <button type="submit" class="btn btn-primary">Login <i class="fa fa-sign-in" aria-hidden="true"></i></button>
  </div>
</div>
